mapper.update:

		update sleep_tab set s_subject=#{s_subject}, s_content=#{s_content},
		s_sleep_type=#{s_sleep_type}, s_major=#{s_major}, s_start_date=#{s_start_date}, s_end_date=#{s_end_date}
		 where s_board_num=#{s_board_num}

 
		update sleep_tab set 
 		s_confirm=#{s_confirm}
 		where s_board_num=#{s_board_num}

 
		update sleep_tab set 
 		s_confirm=#{s_confirm}
 		where s_board_num=#{s_board_num}

 
		update sleep_tab set 
 		s_cancel=#{s_cancel}
 		where s_board_num=#{s_board_num}
